/**
 * tetrad letters ordered by northing then easting (steps of 2000m)
 * i.e. (y*5) + x
 * 
 * where x and y are integer of (10km remainder / 2)
 * 
 * @type {string}
 */
BsbiDb.MappingUtils.tetradLettersRowFirst = 'AFKQVBGLRWCHMSXDINTYEJPUZ';

BsbiDb.MappingUtils.irishGrid = {
     0 : ['V','Q','L','F','A'],
     1 : ['W','R','M','G','B'],
     2 : ['X','S','N','H','C'],
     3 : ['Y','T','O','J','D']
};

/**
 * 
 * @param {number} easting
 * @param {number} northing
 * @return {string} tetrad letter
 */
BsbiDb.MappingUtils.calculate_tetrad = function(easting, northing) {
	return (easting >= 0 && northing >= 0) ?
		BsbiDb.MappingUtils.tetradLetters.charAt((Math.floor(easting % 10000 / 2000) * 5) + Math.floor(northing % 10000 / 2000)) :
		'';
};

/**
 * 
 * @param {number} easting metres
 * @param {number} northing metres
 * @return {string} hectad
 */
BsbiDb.MappingUtils.irish_coords_to_hectad = function(easting, northing) {
	var hundredkmE = Math.floor(easting / 100000), hundredkmN = Math.floor(northing / 100000);
	if (BsbiDb.MappingUtils.irishGrid[hundredkmE] && BsbiDb.MappingUtils.irishGrid[hundredkmE][hundredkmN]) {
		return (BsbiDb.MappingUtils.irishGrid[hundredkmE][hundredkmN]) + Math.floor((easting % 100000)/10000) + Math.floor((northing % 100000)/10000);
	} else {
		return '';
	}
};

/**
 * 
 * @param {number} easting
 * @param {number} northing
 * @returns {?{letter: string, e: number, n: number}}
 */
BsbiDb.MappingUtils.split_ie_coords = function(easting, northing) {
	var hundredkmE = Math.floor(easting / 100000);
	var hundredkmN = Math.floor(northing / 100000);
	if (BsbiDb.MappingUtils.irishGrid[hundredkmE] && BsbiDb.MappingUtils.irishGrid[hundredkmE][hundredkmN]) {
		return {letter: BsbiDb.MappingUtils.irishGrid[hundredkmE][hundredkmN], e: Math.floor(easting % 100000), n: Math.floor(northing % 100000)};
	} else {
		return null;
	}
};

/**
 * 
 * @param {number} easting
 * @param {number} northing
 * @return {string} hectad
 */
BsbiDb.MappingUtils.gb_coords_to_hectad = function(easting, northing) {
	var hundredkmE = easting / 100000 | 0; // Math.floor(easting / 100000);
	var hundredkmN = northing / 100000 | 0; // Math.floor(northing / 100000);
	var firstLetter = "";
	if (hundredkmN < 5) {
		if (hundredkmE < 5) {
			firstLetter = "S";
		} else {
			firstLetter = "T";
		}
	} else if (hundredkmN < 10) {
		if (hundredkmE < 5) {
			firstLetter = "N";
		} else {
			firstLetter = "O";
		}
	} else {
		if (hundredkmE < 5) {
			firstLetter = "H";
		} else {
			firstLetter = "J";
		}	
	}

	var index = 65 + ((4 - (hundredkmN % 5)) * 5) + (hundredkmE % 5);
	
	if (index >= 73) {
		index++;
	}
	
	return firstLetter + 
		String.fromCharCode(index) + // secondLetter
		(((easting - (100000 * hundredkmE)) / 10000) | 0) + 
		(((northing - (100000 * hundredkmN)) / 10000) | 0);
};

