/**
 * converts lat and lon to CI northings and eastings
 * 
 * @returns BsbiDb.OSCIRef
 */
BsbiDb.CILatLng.prototype.to_os_coords = function() {
    //var deg2rad = Math.PI / 180;
    //var rad2deg = 180.0 / Math.PI;

    var phi = this.lat * deg2rad; // convert latitude to radians
    var lam = this.lng * deg2rad; // convert longitude to radians
    var a = 6378388.000;      // OSI semi-major
    var b = 6356911.946;      // OSI semi-minor
    var e0 = 500000;          // OSI easting of false origin
    var n0 = 0;          // OSI northing of false origin
    var f0 = 0.9996;        // OSI scale factor on central meridian
    var e2 = 0.0067226700223333;   // OSI eccentricity squared
    var lam0 = -0.0523598775598;   // OSI false east
    var phi0 = 0;    // OSI false north
    var af0 = a * f0;
    var bf0 = b * f0;

    // easting
    var slat2 = Math.sin(phi) * Math.sin(phi);
    var nu = af0 / (Math.sqrt(1 - (e2 * (slat2))));
    var rho = (nu * (1 - e2)) / (1 - (e2 * slat2));
    var eta2 = (nu / rho) - 1;
    var p = lam - lam0;
    var IV = nu * Math.cos(phi);
    var clat3 = Math.pow(Math.cos(phi), 3);
    var tlat2 = Math.tan(phi) * Math.tan(phi);
    var V = (nu / 6) * clat3 * ((nu / rho) - tlat2);
    var clat5 = Math.pow(Math.cos(phi), 5);
    var tlat4 = Math.pow(Math.tan(phi), 4);
    var VI = (nu / 120) * clat5 * ((5 - (18 * tlat2)) + tlat4 + (14 * eta2) - (58 * tlat2 * eta2));
    var east = e0 + (p * IV) + (Math.pow(p, 3) * V) + (Math.pow(p, 5) * VI);

    // northing
    var n = (af0 - bf0) / (af0 + bf0);
    var M = BsbiDb.OSGB36LatLng._Marc(bf0, n, phi0, phi);
    var I = M + (n0);
    var II = (nu / 2) * Math.sin(phi) * Math.cos(phi);
    var III = ((nu / 24) * Math.sin(phi) * Math.pow(Math.cos(phi), 3)) * (5 - Math.pow(Math.tan(phi), 2) + (9 * eta2));
    var IIIA = ((nu / 720) * Math.sin(phi) * clat5) * (61 - (58 * tlat2) + tlat4);
    var north = I + ((p * p) * II) + (Math.pow(p, 4) * III) + (Math.pow(p, 6) * IIIA);

    //return {x: Math.round(east), y: Math.round(north)};
	return new BsbiDb.OSCIRef(Math.round(east), Math.round(north));
};


/**
 * represents lat lng
 * 
 * @param {number} lat
 * @param {number} lng
 * @constructor
 */
BsbiDb.LatLng = function (lat, lng) {
	this.lat = lat;
	this.lng = lng;
};

BsbiDb.LatLng.transform = function(lat, lon, a, e, h, a2, e2, xp, yp, zp, xr, yr, zr, s) {
	// convert to cartesian; lat, lon are radians
	var sf = s * 0.000001;
	var v = a / (Math.sqrt(1 - (e *(Math.sin(lat) * Math.sin(lat)))));
	var x = (v + h) * Math.cos(lat) * Math.cos(lon);
	var y = (v + h) * Math.cos(lat) * Math.sin(lon);
	var z = ((1 - e) * v + h) * Math.sin(lat);
	// transform cartesian
	var xrot = (xr / 3600) * deg2rad;
	var yrot = (yr / 3600) * deg2rad;
	var zrot = (zr / 3600) * deg2rad;
	var hx = x + (x * sf) - (y * zrot) + (z * yrot) + xp;
	var hy = (x * zrot) + y + (y * sf) - (z * xrot) + yp;
	var hz = (-1 * x * yrot) + (y * xrot) + z + (z * sf) + zp;
	// Convert back to lat, lon
	lon = Math.atan(hy / hx);
	var p = Math.sqrt((hx * hx) + (hy * hy));
	lat = Math.atan(hz / (p * (1 - e2)));
	v = a2 / (Math.sqrt(1 - e2 * (Math.sin(lat) * Math.sin(lat))));
	var errvalue = 1.0;
	var lat0 = 0;
	while (errvalue > 0.001) {
		lat0 = Math.atan((hz + e2 * v * Math.sin(lat)) / p);  
		errvalue = Math.abs(lat0 - lat);
		lat = lat0;
	}
	//h = p / Math.cos(lat) - v;
	//var geo = { latitude: lat, longitude: lon, height: h };  // object to hold lat and lon
	return(new BsbiDb.LatLng(lat, lon));
};

//helper
BsbiDb.OSGB36LatLng._Marc = function (bf0, n, phi0, phi) {
    return bf0 * (((1 + n + ((5 / 4) * (n * n)) + ((5 / 4) * (n * n * n))) * (phi - phi0))
- (((3 * n) + (3 * (n * n)) + ((21 / 8) * (n * n * n))) * (Math.sin(phi - phi0)) * (Math.cos(phi + phi0)))
+ ((((15 / 8) * (n * n)) + ((15 / 8) * (n * n * n))) * (Math.sin(2 * (phi - phi0))) * (Math.cos(2 * (phi + phi0))))
- (((35 / 24) * (n * n * n)) * (Math.sin(3 * (phi - phi0))) * (Math.cos(3 * (phi + phi0)))));
};

//converts lat and lon (OSGB36) to OS northings and eastings
BsbiDb.OSGB36LatLng.prototype.to_os_coords = function() {
    var phi = this.lat * deg2rad; // convert latitude to radians
    var lam = this.lng * deg2rad; // convert longitude to radians
    var a = 6377563.396; // OSGB semi-major axis
    var b = 6356256.91; // OSGB semi-minor axis
    var e0 = 400000; // easting of false origin
    var n0 = -100000; // northing of false origin
    var f0 = 0.9996012717; // OSGB scale factor on central meridian
    var e2 = 0.0066705397616; // OSGB eccentricity squared
    var lam0 = -0.034906585039886591; // OSGB false east
    var phi0 = 0.85521133347722145; // OSGB false north
    var af0 = a * f0;
    var bf0 = b * f0;

    // easting
    var slat2 = Math.sin(phi) * Math.sin(phi);
    var nu = af0 / (Math.sqrt(1 - (e2 * (slat2))));
    var rho = (nu * (1 - e2)) / (1 - (e2 * slat2));
    var eta2 = (nu / rho) - 1;
    var p = lam - lam0;
    var IV = nu * Math.cos(phi);
    var clat3 = Math.pow(Math.cos(phi), 3);
    var tlat2 = Math.tan(phi) * Math.tan(phi);
    var V = (nu / 6) * clat3 * ((nu / rho) - tlat2);
    var clat5 = Math.pow(Math.cos(phi), 5);
    var tlat4 = Math.pow(Math.tan(phi), 4);
    var VI = (nu / 120) * clat5 * ((5 - (18 * tlat2)) + tlat4 + (14 * eta2) - (58 * tlat2 * eta2));
    var east = e0 + (p * IV) + (Math.pow(p, 3) * V) + (Math.pow(p, 5) * VI);

    // northing
    var n = (af0 - bf0) / (af0 + bf0);
    var M = BsbiDb.OSGB36LatLng._Marc(bf0, n, phi0, phi);
    var I = M + (n0);
    var II = (nu / 2) * Math.sin(phi) * Math.cos(phi);
    var III = ((nu / 24) * Math.sin(phi) * Math.pow(Math.cos(phi), 3)) * (5 - Math.pow(Math.tan(phi), 2) + (9 * eta2));
    var IIIA = ((nu / 720) * Math.sin(phi) * clat5) * (61 - (58 * tlat2) + tlat4);
    var north = I + ((p * p) * II) + (Math.pow(p, 4) * III) + (Math.pow(p, 6) * IIIA);

	return new BsbiDb.OSRef(Math.round(east), Math.round(north));
};

/**
 * abstract representation of a gridref co-ordinate pair
 * (*not a gridref string*)
 * 
 * @constructor 
 * @returns {BsbiDb.NationalGridCoords}
 */
BsbiDb.NationalGridCoords = function () {};

/**
 * 
 * @param {string} letters
 * @param {number} e metres
 * @param {number} n metres
 * @param {number} precision metres
 * @returns {String}
 */
BsbiDb.NationalGridCoords._e_n_to_gr = function(letters, e, n, precision) {
	var eString = ('00000' + Math.floor(e));
	var nString = ('00000' + Math.floor(n));
	
	if (precision === 2000) {
		return letters + 
			eString.charAt(eString.length-5) + nString.charAt(nString.length-5) + 
			BsbiDb.MappingUtils.calculate_tetrad(e, n);
	} else if (precision === 100000) {
		return letters;
	} else {
		if (precision === 5000) {
			// ignore quadrant and treat as hectad
			precision = 10000;
		}
	
		var logPrecision = Math.round(Math.log10(precision));
		return letters +
			(logPrecision ? 
				(eString.slice(-5,  -logPrecision) + nString.slice(-5,  -logPrecision))
				:
				(eString.slice(-5) + nString.slice(-5))
			);
	}
};

BsbiDb.NationalGridCoords.prototype.toString = function() {
	return this.x + ',' + this.y;
};

/**
 * 
 * @param {number} easting metres
 * @param {number} northing metres
 * @constructor
 * @extends BsbiDb.NationalGridCoords
 * @returns {BsbiDb.OSRef}
 */
BsbiDb.OSRef = function(easting, northing) {
	this.x = easting;
	this.y = northing;
};

BsbiDb.OSRef.prototype = new BsbiDb.NationalGridCoords();
BsbiDb.OSRef.prototype.constructor = BsbiDb.OSRef;
BsbiDb.OSRef.prototype.country = 'GB';

/**
 * 
 * @param {number} precision metres
 * @returns {String}
 */
BsbiDb.OSRef.prototype.to_gridref = function (precision) {
	var hundredkmE = this.x / 100000 | 0; // Math.floor(this.x / 100000);
	var hundredkmN = this.y / 100000 | 0; // Math.floor(this.y / 100000);
	var firstLetter = '';
	if (hundredkmN < 5) {
		if (hundredkmE < 5) {
			firstLetter = "S";
		} else {
			firstLetter = "T";
		}
	} else if (hundredkmN < 10) {
		if (hundredkmE < 5) {
			firstLetter = "N";
		} else {
			firstLetter = "O";
		}
	} else {
		if (hundredkmE < 5) {
			firstLetter = "H";
		} else {
			firstLetter = "J";
		}
	}

	var secondLetter = '';
	var index = 65 + ((4 - (hundredkmN % 5)) * 5) + (hundredkmE % 5);
	
	if (index >= 73) {
		index++;
	}
	
	secondLetter = String.fromCharCode(index);
	
	return BsbiDb.NationalGridCoords._e_n_to_gr(
		firstLetter + secondLetter, 
		(this.x - (100000 * hundredkmE)),
		(this.y - (100000 * hundredkmN)),
		precision ? precision : 1
	);
};

BsbiDb.OSRef.prototype.is_gb_hectad = function() {
	return BsbiDb.MappingUtils.gbHectads.indexOf(BsbiDb.MappingUtils.gb_coords_to_hectad(this.x, this.y)) !== -1;
};

/**
 * convert easting,northing to a WGS84 lat lng
 * 
 * @returns {BsbiDb.WGS84LatLng}
 */
BsbiDb.OSRef.prototype.to_latLng = function() {
	//airy1830 = RefEll::airy1830(); //new RefEll(6377563.396, 6356256.909);
	//var OSGB_F0  = 0.9996012717;
	//var N0       = -100000.0;
	var E0       = 400000.0;
	var phi0     = 0.85521133347722; //deg2rad(49.0);
	var lambda0  = -0.034906585039887; //deg2rad(-2.0);
	var a        = 6377563.396; // airy1830->maj;
	//var b        = 6356256.909; // airy1830->min;
	var eSquared = 0.00667054007; // ((maj * maj) - (min * min)) / (maj * maj); // airy1830->ecc;
	var phi      = 0.0;
	var lambda   = 0.0;
	var E        = this.x;
	var N        = this.y;
	var n        = 0.0016732203289875; //(a - b) / (a + b);
	var M;
	var phiPrime = ((N + 100000) / (a * 0.9996012717)) + phi0;

	// 15 / 8 === 1.875
	// 5 / 4 === 1.25
	// 21 / 8 === 2.625
	
	do {
		M = N + 100000 - (
			6353722.489 // (b * OSGB_F0)
			* ((1.0016767257674 // * (((1 + n + (1.25 * n * n) + (1.25 * n * n * n))
			* (phiPrime - phi0))
			- (0.00502807228247412 // - (((3 * n) + (3 * n * n) + (2.625 * n * n * n))
			* Math.sin(phiPrime - phi0)
			* Math.cos(phiPrime + phi0))
			+ (((1.875 * n * n) + (1.875 * n * n * n))
			* Math.sin(2.0 * (phiPrime - phi0))
			* Math.cos(2.0 * (phiPrime + phi0)))
			- (((35.0 / 24.0) * n * n * n)
			* Math.sin(3.0 * (phiPrime - phi0))
			* Math.cos(3.0 * (phiPrime + phi0)))));
	
		phiPrime += M / 6375020.48098897; // (N - N0 - M) / (a * OSGB_F0);
	} while (M >= 0.001);

	var sinphiPrime2 = Math.sin(phiPrime) * Math.sin(phiPrime);
	var tanphiPrime2 = Math.tan(phiPrime) * Math.tan(phiPrime);
	var secphiPrime = 1.0 / Math.cos(phiPrime);

	var v = a * 0.9996012717 * Math.pow(1.0 - eSquared * sinphiPrime2, -0.5);

	var rho =
		a
		* 0.9996012717
		* (1.0 - eSquared)
		* Math.pow(1.0 - eSquared * sinphiPrime2, -1.5);
	var etaSquared = (v / rho) - 1.0;
	var VII = Math.tan(phiPrime) / (2 * rho * v);
	var VIII =
		(Math.tan(phiPrime) / (24.0 * rho * Math.pow(v, 3.0)))
		* (5.0
		+ (3.0 * tanphiPrime2)
		+ etaSquared
		- (9.0 * tanphiPrime2 * etaSquared));
	var IX =
		(Math.tan(phiPrime) / (720.0 * rho * Math.pow(v, 5.0)))
		* (61.0
		+ (90.0 * tanphiPrime2)
		+ (45.0 * tanphiPrime2 * tanphiPrime2));
	var X = secphiPrime / v;
	var XI =
		(secphiPrime / (6.0 * v * v * v))
		* ((v / rho) + (2 * tanphiPrime2));
	var XII =
		(secphiPrime / (120.0 * Math.pow(v, 5.0)))
		* (5.0
		+ (28.0 * tanphiPrime2)
		+ (24.0 * tanphiPrime2 * tanphiPrime2));
	var XIIA =
		(secphiPrime / (5040.0 * Math.pow(v, 7.0)))
		* (61.0
		+ (662.0 * tanphiPrime2)
		+ (1320.0 * tanphiPrime2 * tanphiPrime2)
		+ (720.0
		* tanphiPrime2
		* tanphiPrime2
		* tanphiPrime2));
	phi =
		phiPrime
		- (VII * Math.pow(E - E0, 2.0))
		+ (VIII * Math.pow(E - E0, 4.0))
		- (IX * Math.pow(E - E0, 6.0));
	lambda =
		lambda0
		+ (X * (E - E0))
		- (XI * Math.pow(E - E0, 3.0))
		+ (XII * Math.pow(E - E0, 5.0))
		- (XIIA * Math.pow(E - E0, 7.0));

	//var ll = new BsbiDb.OSGB36LatLng(rad2deg * phi, rad2deg * lambda); // airy 1830
	//ll.OSGB36_to_WGS84(); // google earth uses WGS84

	//return ll;
	return (new BsbiDb.OSGB36LatLng(rad2deg * phi, rad2deg * lambda)).to_WGS84();
};

